function addArticle(e) {
  //Create HTML elements
  let article = document.createElement("article");
  let author = document.createElement("p");
  let title = document.createElement("h3");
  let data1 = document.createElement("small");
  let data2 = document.createElement("small");
  let icon1 = document.createElement("span");
  let icon2 = document.createElement("span");

  //Define CSS class lists
  let articleStyle = ["bg-gray-100", "p-4", "rounded", "shadow", "text-gray-700", "text-sm", "flex", "flex-col", "gap-2"];
  let authorStyle = ["uppercase", "text-sm"];
  let titleStyle = ["text-xl", "text-blue-dark", "font-bold"];
  let dataStyle = ["block"];
  let iconStyle = ["material-icons", "text-base", "align-text-bottom", "mr-1"];



  //Add CSS class to HTML elements
  article.classList.add(...articleStyle);
  author.classList.add(...authorStyle);
  title.classList.add(...titleStyle);
  data1.classList.add(...dataStyle);
  data2.classList.add(...dataStyle);
  icon1.classList.add(...iconStyle);
  icon2.classList.add(...iconStyle);


  let authorText = document.createTextNode(e.autor)
  author.appendChild(authorText);
  article.appendChild(author);

  let titleText = document.createTextNode(e.titulo);
  title.appendChild(titleText);
  article.appendChild(title);

  let iconFuente = document.createTextNode("book");
  icon1.append(iconFuente);
  data1.append(icon1);

  let dataFuente = e.fuente;
  data1.append(dataFuente);
  article.appendChild(data1);

  let iconFecha = document.createTextNode("today");
  icon2.append(iconFecha);
  data2.append(icon2);

  let dataFecha = e.fecha;
  data2.append(dataFecha);
  article.appendChild(data2);

  //Append articles to list
  let articleList = document.querySelector("#article-list");
  articleList.append(article);
}

data.forEach(e => addArticle(e));
